import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import libraryShuffled from './library.js';
import Card from './Card/Card';


class App extends Component {
    state = {
      feedContent: [],
      otherCards: [...libraryShuffled],
    }

    addCardToFeed = () => {
      let newFeedContent = this.state.otherCards[0];
      let otherCardsArray = [...this.state.otherCards];
      otherCardsArray.shift()
      this.setState({feedContent: [...this.state.feedContent + newFeedContent], 
                     otherCards: [...otherCardsArray]});
    };

    addCardsHandler = (amountToAdd) => {
      if (this.state.otherCards > amountToAdd){
        amountToAdd.forEach((i) => {
          this.addCardToFeed();
        })} else {
        alert("Looks like you've seen almost everything already. You must be pretty inspired now. Come back later for more.");
      }
    };

  render() {

    let feed = null;

    if (this.state.feedContent) {
      feed = (
          <div>
            {this.state.feedContent.map((feedItem) => {
                return <Card 
                title={feedItem.title}
                desc={feedItem.desc}
                video={feedItem.video}
                image={feedItem.image}
                quote={feedItem.quote}
                key={feedItem.key}
                />
              })}
          </div>
      )
      console.log(feed);
    }

    let buttons = (
      <div className="buttons">
        {this.state.otherCards >= 1 ? <button className="addCard" onClick={() => this.addCardsHandler(1)}>+1 Dose of Inspiration</button> : null}
        {this.state.otherCards >= 5 ? <button className="addCard" onClick={() => this.addCardsHandler(5)}>+5 Doses of Inspiration</button> : null}
        {this.state.otherCards >= 1 ? <button className="clearCards" onClick={() => this.setState({ feedContent:[]})}>Clear All</button> : null}
      </div>
    );

  return (<div className="App">
    <Header />
    <div className="mainContainer">
    {feed}
    </div>

    {buttons}    
    </div>
  );
}
}

export default App;
