import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import library from './library.js';
import Card from './Card/Card';





class App extends Component {
  state = {
    feedContent: ['item1', 'item2'],
  }

  getRadomNumber = (max) =>{
    max = Math.floor(max);
    return Math.floor(Math.random() * (max + 1));
  }

  selectRandomMedia = (fromHere) => {
    let fromHereArrayLength = (fromHere.length - 1);
    let i = this.getRadomNumber(fromHereArrayLength);
    return fromHere[i];
  }

  newRadomCardHandler = () => {
    console.log("has been executed")
    let allMedia = [...library];
    let newFeedContent = this.selectRandomMedia(allMedia);
    this.setState({feedContent: newFeedContent});

  };



  render() {

    // let feed = null

    // if (this.state.feedContent){
    //   this.state.feedContent.map((feedItem, index) => {
    //           return <Card 
    //           title={feedItem.title}
    //           desc={feedItem.desc}
    //           video={feedItem.video}
    //           image={feedItem.image}
    //           quote={feedItem.quote}
    //           key={feedItem.index}
    //            />
    //         })}


  return (<div className="App">
    <Header />
    <div className="mainContainer">
      {this.state.feedContent.map((feedItem, index) => {
              return <Card 
              title={feedItem.title}
              desc={feedItem.desc}
              video={feedItem.video}
              image={feedItem.image}
              quote={feedItem.quote}
              key={feedItem.index}
               />
            })}
    </div>
    <button onClick={this.newRadomCardHandler}>Load Radom Inspiration</button>
    <button onClick={this.newRadomCardHandler}>Load All Inspiration</button>
    </div>
  );
}
}

export default App;
