import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import library from './library.js';
import Card from './Card/Card';


class App extends Component {
  state = {
    feedContent: null,
  }

  getRadomNumber = (max) =>{
    max = Math.floor(max);
    return Math.floor(Math.random() * (max + 1));
  }

  selectRandomMedia = (fromHere) => {
    let fromHereArrayLength = (fromHere.length - 1);
    let i = this.getRadomNumber(fromHereArrayLength);
    return fromHere[i];
  }

  newRadomCardHandler = () => {
    let allMedia = [...library];
    let newFeedItem = (this.selectRandomMedia(allMedia));
    let newFeedContent = [newFeedItem];
    console.log(newFeedContent);
    this.setState({feedContent: newFeedContent});
  };

  newRadomCardHandler = () => {
    let allMedia = [...library];
    let newFeedItem = (this.selectRandomMedia(allMedia));
    let newFeedContent = [newFeedItem];
    console.log(newFeedContent);
    this.setState({feedContent: newFeedContent});
  };

  addCardsHandler = (amountToAdd) => {
  }

  render() {

  let feed = null;

  if (this.state.feedContent) {
    feed = (
        <div>
          {this.state.feedContent.map((feedItem) => {
              return <Card 
              title={feedItem.title}
              desc={feedItem.desc}
              video={feedItem.video}
              image={feedItem.image}
              quote={feedItem.quote}
              key={feedItem.index}
               />
            })}
        </div>
    )
    console.log(feed);
  }

  return (<div className="App">
    <Header />
    <div className="mainContainer">
    {feed}
    </div>
    <button class="addCard" onClick={this.newRadomCardHandler}>+1 Dose of Inspiration</button>
    <button class="addCard" onClick={() => this.addCardsHandler(5)}>+5 Doses of Inspiration</button>
    <button class="clearCards" onClick={() => this.setState({ feedContent: null})}>Clear All</button>
    </div>
  );
}
}

export default App;
