import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import libraryShuffled from './library.js';
import Card from './Card/Card';


class App extends Component {
    state = {
      feedContent: [],
      otherCards: [...libraryShuffled],
    }

    addCardToFeed = () => {
      let feedContentArray = ([...this.state.feedContent]);
      let otherCardsArray = [...this.state.otherCards];
      let newCard = this.state.otherCards[0];

      feedContentArray.push(newCard);
      otherCardsArray.shift()

      this.setState({feedContent: [...feedContentArray], 
                     otherCards: [...otherCardsArray]});
    };


    addCardsHandler = (amountToAdd) => {
      if (this.state.otherCards.length >= amountToAdd){
          for (let i = amountToAdd; i >= 0; i--) {
            this.addCardToFeed();
          }} else {
            alert("Looks like you've seen almost everything already. You must be pretty inspired now. Come back later for more.");
      }
    };

  render() {

    console.log("STATE: FEED CONTENT ");
    console.log(this.state.feedContent)
    console.log("STATE: OTHERCARDS ");
    console.log(this.state.otherCards)

    let feed = null;

    if (this.state.feedContent) {
      feed = (
          <div>
            {this.state.feedContent.map((feedItem) => {
                return <Card 
                title={feedItem.title}
                desc={feedItem.desc}
                video={feedItem.video}
                image={feedItem.image}
                quote={feedItem.quote}
                key={feedItem.key}
                />
              })}
          </div>
      )
    }

    let buttons = (
      <div className="buttons">
        {this.state.otherCards.length >= 1 ? <button className="addCard" onClick={() => this.addCardsHandler(1)}>+1 Dose of Inspiration</button> : null}
        {this.state.otherCards.length >= 5 ? <button className="addCard" onClick={() => this.addCardsHandler(5)}>+5 Doses of Inspiration</button> : null}
        {this.state.feedContent.length >= 1 ? <button className="clearCards" onClick={() => this.setState({ feedContent:[], otherCards: [...libraryShuffled] })}>Clear All</button> : null}
      </div>
    );

  return (<div className="App">
    <Header />
    <div className="mainContainer">
    {feed}
    </div>

    {buttons}    
    </div>
  );
}
}

export default App;
