import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import library from './library.js';
import Card from './Card/Card';

function getRadomNumber(max){
  max = Math.floor(max);
  return Math.floor(Math.random() * (max + 1));
}

function selectRandomMedia(fromHere){
  let fromHereArrayLength = (fromHere.length - 1);
  let i = getRadomNumber(fromHereArrayLength);
  return fromHere[i];
}

function newRadomCardHandler(){
  console.log("has been executed")
  let allMedia = [...library];
  let newFeedContent = selectRandomMedia(allMedia);
  this.setState(newFeedContent);
};



class App extends Component {
  state = {
    feedContent: [
      {title : "Radom Inspirational Conent",
      desc: "push a button to get started" }
    ],

  }





  render() {

      let feed = (        
        <div>

          {this.state.feedContent.map((feedItem) => {
              return <Card 
              title={feedItem.title}
              desc={feedItem.desc}
              video={feedItem.video}
              image={feedItem.image}
              quote={feedItem.quote}
              key={feedItem.index}
               />
            })}
        </div>)


  return (<div className="App">
    <Header />
    <div className="mainContainer">
      {feed}
    </div>
    <button onClick={newRadomCardHandler()}>Load Radom Inspiration</button>
    <button onClick={newRadomCardHandler()}>Load All Inspiration</button>
    </div>
  );
}
}

export default App;
