import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header';
import library from './library.js';
import Card from './Card/Card';





class App extends Component {
  state = {
    feedContent: null,
  }

  getRadomNumber = (max) =>{
    max = Math.floor(max);
    return Math.floor(Math.random() * (max + 1));
  }

  selectRandomMedia = (fromHere) => {
    let fromHereArrayLength = (fromHere.length - 1);
    let i = this.getRadomNumber(fromHereArrayLength);
    return fromHere[i];
  }

  newRadomCardHandler = () => {
    let allMedia = [...library];
    let newFeedItem = (this.selectRandomMedia(allMedia));
    let newFeedContent = [newFeedItem];
    console.log(newFeedContent);
    this.setState({feedContent: newFeedContent});
  };


  createCard = (fromThis) => {
    fromThis.map((feedItem ) => {
      return <Card 
      title={feedItem.title}
      desc={feedItem.desc}
      video={feedItem.video}
      image={feedItem.image}
      quote={feedItem.quote}
      key={feedItem.index}
       />
    })
  }




  render() {

  let feed = null;

  if (this.state.feedContent) {
    console.log("feedContent is not null");
    feed = (
        <div>
          {this.state.feedContent.map((feedItem) => {
              return <Card 
              title={feedItem.title}
              desc={feedItem.desc}
              video={feedItem.video}
              image={feedItem.image}
              quote={feedItem.quote}
              key={feedItem.index}
               />
            })}
        </div>


    )
  }

  return (<div className="App">
    <Header />
    <div className="mainContainer">
      {/* {this.state.feedContent} */}
    </div>
    <button onClick={this.newRadomCardHandler}>Load Random Inspiration</button>
    <button onClick={this.newRadomCardHandler}>Load All Inspiration</button>
    </div>
  );
}
}

export default App;
